(192, '2017中国机器人大赛比赛通知', 'static/kindeditor/attached/image/20170607/69a2de47b925bbb0103459d0ddc3adc2.jpg', 101, '', '中国自动化学会', '', 498, 1, '', '', '', 1496627940, '<p align="center">\n	<strong><span style="color:#E53333;font-size:32px;">中国自动化学会</span></strong> \n</p>\n<p align="center">\n	<strong><span style="font-size:32px;"> </span></strong> \n</p>\n<hr />\n<strong> \n<div style="text-align:center;">\n	<strong style="line-height:1.5;"><span style="font-size:24px;">2017中国机器人大赛</span></strong> \n</div>\n</strong> \n<p align="center">\n	<span style="font-size:16px;">2017年8月14-17日 &nbsp;山东 日照</span><span style="font-size:16px;">&nbsp;</span> \n</p>\n<p>\n	<span style="font-size:14px;"><strong>主办单位</strong></span> \n</p>\n<blockquote style="margin-right:0px;" dir="ltr">\n	<p>\n		<span style="font-size:14px;">中国自动化学会</span> \n	</p>\n	<p>\n		<span style="font-size:14px;">日照市人民政府</span> \n	</p>\n</blockquote>\n<p>\n	<span style="font-size:14px;"><strong>承办单位</strong></span> \n</p>\n<blockquote style="margin-right:0px;" dir="ltr">\n	<p>\n		<span style="font-size:14px;">中国自动化学会机器人竞赛工作委员会</span> \n	</p>\n	<p>\n		<span style="font-size:14px;">中国自动化学会机器人竞赛与培训部</span> \n	</p>\n	<p>\n		<span style="font-size:14px;">日照高新技术产业开发区管委会</span> \n	</p>\n</blockquote>\n<p>\n	<span style="font-size:14px;"><strong>协办单位</strong></span> \n</p>\n<blockquote style="margin-right:0px;" dir="ltr">\n	<p>\n		<span style="font-size:14px;">教育部高等学校自动化类专业教学指导委员会</span> \n	</p>\n	<p>\n		<span style="font-size:14px;">日照市科学技术局</span> \n	</p>\n	<p>\n		<span style="font-size:14px;">日照市科学技术协会</span> \n	</p>\n	<p>\n		<span style="font-size:14px;">日照高新发展集团有限公司</span> \n	</p>\n</blockquote>\n<p>\n	<span style="font-size:14px;"><strong>支持单位</strong></span> \n</p>\n<blockquote style="margin-right:0px;" dir="ltr">\n	<p>\n		<span style="font-size:14px;">中国自动化学会智能自动化专业委员会</span> \n	</p>\n</blockquote>\n<p>\n	<span style="font-size:14px;">&nbsp;</span> \n</p>\n<p>\n	<span style="font-size:14px;"><strong>大赛顾问</strong>