INSERT INTO `web_news` (`id`, `title`, `simg`, `bid`, `author`, `source`, `descs`, `ord`, `is_recom`, `seoTitle`, `seoKeyword`, `setDescription`, `addtime`, `con_1`, `con_2`, `con_3`, `con_4`) VALUES
(232, '小型组', '', 115, '', '', '', 5, 0, '', '', '', 1491357420, '<h1 class="page-title" style="text-align:center;box-sizing:border-box;font-size:36px;margin:0px;font-family:\'Helvetica Neue\', Roboto, sans-serif;line-height:1;color:#333333;white-space:normal;background-color:#F5F6FA;">\n	RoboCup足球小型组\n</h1>\n<div>\n	<p style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;font-family:\'Helvetica Neue\', Roboto, sans-serif;font-size:14px;line-height:24px;white-space:normal;">\n		<span style="box-sizing:border-box;color:#666666;font-family:arial, sans-serif;font-size:18px;line-height:21px;text-align:justify;">The Small Size league or F180 league as it is otherwise known, is one of the oldest RoboCup Soccer leagues. It focuses on the problem of intelligent multi-robot/agent cooperation and control in a highly dynamic environment with a hybrid centralized/distributed system.</span>\n	</p>\n	<p style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;font-family:\'Helvetica Neue\', Roboto, sans-serif;font-size:14px;line-height:24px;white-space:normal;">\n		<a href="http://wiki.robocup.org/wiki/Small_Size_League" target="_blank" style="box-sizing:border-box;color:#D10400;text-decoration:none;margin:0px;padding:0px;outline:0px;cursor:pointer;font-family:arial, sans-serif;font-size:18px;line-height:21px;">Soccer Small Size League</a>\n	</p>\n	<p style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;white-space:normal;font-size:16pt;font-family:宋体;line-height:32px;text-indent:2em;">\n		小型组足球机器人比赛是RoboCup机器人足球世界杯的主要项目之一。本次比赛采用两队各3台实物移动机器人在约4m×6m的场地上进行机器人足球比赛。规则及赛制均参考“RoboCup机器人世界杯小型机器人足球比赛”规则制定。参赛队参赛机器人须采用由比赛组委会指定的标准机器人或其他比赛组委会认可的机器人，通过编写比赛策略和运动规划算法与代码，两两对抗，以在规定时间内进球数计算胜负。\n	</p>\n	<p style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;white-space:normal;font-size:16pt;font-family:宋体;line-height:32px;text-indent:2em;">\n		小型足球机器人是集图像处理、自动决策、路径规划、自主避障、c++/lua混合程序设计于一体的轮式足球机器人。集中解决了多个智能机器人之间的协同合作以及在混合集中分布式系统下高度动态环境中的控制问题。\n	</p>\n<br />\n</div>', '<span style="font-size:16px;">点击下载</span><br />\n<span style="font-size:16px;"></span><a class="ke-insertfile" href="/static/kindeditor/attached/file/20170608/20170608095943_89278.pdf" target="_blank"><span style="font-size:16px;">2017RoboCup机器人世界杯中国赛RoboCup小型组规则.pdf</span></a><span style="font-size:16px;"></span>', '<span style="font-size:16px;">点击下载</span><br />\n<span style="font-size:16px;"></span><a class="ke-insertfile" href="/static/kindeditor/attached/file/20170608/20170608100010_36009.pdf" target="_blank"><span style="font-size:16px;">2017RoboCup机器人世界杯中国赛RoboCup小型组项目赛程.pdf</span></a><span style="font-size:16px;"></span>', '<p class="MsoNormal" align="center" style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;font-family:\'Helvetica Neue\', Roboto, sans-serif;font-size:14px;line-height:24px;white-space:normal;text-align:center;">\n	<span lang="EN-US" style="box-sizing:border-box;font-size:18pt;">2017RoboCup</span><span style="box-sizing:border-box;font-size:18pt;font-family:宋体;">机器人世界杯中国赛</span>\n</p>\n<p class="MsoNormal" align="center" style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;font-family:\'Helvetica Neue\', Roboto, sans-serif;font-size:14px;line-height:24px;white-space:normal;text-align:center;">\n	<span lang="EN-US" style="box-sizing:border-box;font-size:18pt;">RoboCup</span><span style="box-sizing:border-box;font-size:18pt;font-family:宋体;">小型组获奖名单</span>\n</p>\n<div align="center" style="box-sizing:border-box;color:#333333;font-family:\'Helvetica Neue\', Roboto, sans-serif;font-size:14px;line-height:24px;white-space:normal;">\n	<table class="MsoTableGrid" border="1" width="669" cellspacing="0" cellpadding="0" style="box-sizing:border-box;border-spacing:0px;width:715px;border:none;height:651px;">\n		<tbody style="box-sizing:border-box;">\n			<tr style="box-sizing:border-box;">\n				<td width="148" style="box-sizing:border-box;padding: