INSERT INTO `web_news` (`id`, `title`, `simg`, `bid`, `author`, `source`, `descs`, `ord`, `is_recom`, `seoTitle`, `seoKeyword`, `setDescription`, `addtime`, `con_1`, `con_2`, `con_3`, `con_4`) VALUES
(233, '标准平台组', '', 115, '', '', '', 6, 0, '', '', '', 1491357600, '<p style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;font-family:\'Helvetica Neue\', Roboto, sans-serif;font-size:14px;line-height:24px;white-space:normal;">\n	<span style="box-sizing:border-box;color:#666666;font-family:arial, sans-serif;font-size:18px;line-height:21px;text-align:justify;">In this league all teams use identical (i.e. standard) robots. Therefore the teams concentrate on software development only, while still using state-of-the-art robots. Omnidirectional vision is not allowed, forcing decision-making to trade vision resources for self-localization and ball localization.&nbsp;</span><br style="box-sizing:border-box;margin:0px;padding:0px;outline:0px;color:#666666;font-family:arial, sans-serif;font-size:18px;line-height:21px;text-align:justify;" />\n<span style="box-sizing:border-box;color:#666666;font-family:arial, sans-serif;font-size:18px;line-height:21px;text-align:justify;">The league replaced the highly successful Four-Legged League, based on Sony’s AIBO dog robots, and is now based on Aldebaran’s Nao humanoids.</span>\n</p>\n<p style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;font-family:\'Helvetica Neue\', Roboto, sans-serif;font-size:14px;line-height:24px;white-space:normal;">\n	<a href="http://wiki.robocup.org/wiki/Standard_Platform_League" target="_blank" style="box-sizing:border-box;color:#D10400;text-decoration:none;margin:0px;padding:0px;outline:0px;cursor:pointer;font-family:arial, sans-serif;font-size:18px;line-height:21px;">Soccer Standard Platform League</a>\n</p>\n<p style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;white-space:normal;font-size:16pt;font-family:宋体;line-height:32px;text-indent:2em;">\n	RoboCup 标准平台组比赛是RoboCup 传统比赛项目，最初名为RoboCuo 四足组（1998‐2004），采用AIBO 四足机器人作为统一的参赛机器人平台。2004 年该比赛更名为标准平台组比赛。2007 年改为采用Nao 双足机器人作为统一参赛机器人平台。该项比赛已成为历 届RoboCup 比赛中最受关注的赛事之一，2016 年共有24 支队伍获得参赛资格，参加了在德国莱比锡举行的国际比赛，德国不来梅大学的B‐Human 队获得世界冠军。2016 年国内比赛共有6 支队伍参加比赛，同济大学队获得冠军。\n</p>\n<p style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;white-space:normal;font-size:16pt;font-family:宋体;line-height:32px;text-indent:2em;">\n	目前该项比赛双方各采用不超过5 台Nao 双足机器人作为比赛队员、1 台Nao 双足机器人作为教练，比赛用球为黑白色足球。比赛上下半场各10 分钟，中场休息10 分钟。在比赛过程中，机器人自主决策、自主运动，是RoboCup 各项赛事中技术含量最高、观赏度最强的比赛之一。\n</p>', '<span style="font-size:16px;">点击下载</span><br />\n<span style="font-size:16px;"></span><a class="ke-insertfile" href="/static/kindeditor/attached/file/20170608/20170608100129_78671.pdf" target="_blank"><span style="font-size:16px;">2017RoboCup机器人世界杯中国赛RoboCup标准平台组规则.pdf</span></a><span style="font-size:16px;"></span>', '<span style="font-size:16px;">点击下载</span><br />\n<span style="font-size:16px;"></span><a class="ke-insertfile" href="/static/kindeditor/attached/file/20170608/20170608100233_56067.pdf" target="_blank"><span style="font-size:16px;">2017RoboCUp机器人世界杯中国赛RoboCup标准平台组项目赛程.pdf</span></a><span style="font-size:16px;"></span>', '<p class="MsoNormal" align="center" style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;font-family:\'Helvetica Neue\', Roboto, sans-serif;font-size:14px;line-height:24px;white-space:normal;text-align:center;">\n	<span lang="EN-US" style="box-sizing:border-box;font-size:18pt;">2017RoboCup</span><span style="box-sizing:border-box;font-size:18pt;font-family:宋体;">机器人世界杯中国赛</span>\n</p>\n<p class="MsoNormal" align="center" style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;font-family:\'Helvetica Neue\', Roboto, sans-serif;font-size:14px;line-height:24px;white-space:normal;text-align:center;">\n	<span lang="EN-US" style="box-sizing:border-box;font-size:18pt;">RoboCup</span><span style="box-sizing:border-box;font-size:18pt;font-family:宋体;">标准平台组获奖名单</span>\n</p>\n<p style="box-sizing:border-box;margin-top:0px;margin-bottom:12px;color:#333333;font-family:\'Helvetica Neue\', Roboto, sans-serif;font-size:14p