INSERT INTO `web_news` (`id`, `title`, `simg`, `bid`, `author`, `source`, `descs`, `ord`, `is_recom`, `seoTitle`, `seoKeyword`, `setDescription`, `addtime`, `con_1`, `con_2`, `con_3`, `con_4`) VALUES
(515, '关于2018中国机器人大赛成都中转志愿者招募的通知', 'static/kindeditor/attached/image/20180806/9d173a4bf104e4affd8cf8f49d8820cc.jpg', 101, '', '', '', 1, 0, '', '', '', 1533549600, '<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">亲爱的小伙伴们：&nbsp;&nbsp;&nbsp;&nbsp;</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;暑假已经过半，你萌还在家里被亲妈嫌弃吗？有木有在家闲到爆炸？有木有感觉非常的无聊？还是说打算在家刷完延禧攻略去学校继续上学？？？</span> \n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 机器人大赛了解一下？要不要加入日程安排？</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;不过，参赛已经来不及了，但是你可以加入我们呀！</span> \n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;现面向所有参赛队员及未来得及报名的伙伴们（成都本地人优先哦）招募12名志愿者，先到先得，择优录取啦~<br style="margin:0px;padding:0px;" />\n</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">工作须知：</span> \n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;text-align:center;"=""> <span style="margin:0px;padding:0px;font-size:16px;">时间：2018年8月11日至8月13日</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;text-align:center;"=""> <span style="margin:0px;padding:0px;font-size:16px;">(具体工作时间以各岗位实际安排为准)</span> \n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;text-align:center;"=""> <span style="margin:0px;padding:0px;font-size:16px;">主要工作地点：成都东站，成都火车站</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""><br />\n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">工作岗位：</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">1、接站引导组</span> \n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">在指定站点接受抵达参赛队的咨询，确认参赛队是否包车，并进行乘车引导；</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">2、乘车登记组</span> \n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">在指定乘车地点进行登记，确定乘车人数。</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""><br />\n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">志愿者福利：</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">① 所有志愿者均提供志愿者证明，并评选出4位优秀志愿者，提供优秀志愿者证书；</span> \n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">② 工作正餐，免费零食，遮阳帽；</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">③ 当然了，还有交通补贴！</span> \n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""><br />\n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">心动了吗？</span> \n</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;padding:0px;font-size:16px;">别犹豫，别错过哈！</span> \n	</p>\n<p style="white-space:normal;margin-top:0px;margin-bottom:0px;padding:0px;clear:both;caret-color:#7E856A;color:#7E856A;font-family:" font-size:14px;"=""> <span style="margin:0px;p